def encode_AES(bfile,AES_key):
    iv = Random.new().read(AES.block_size)
    cipher = AES.new(AES_key, AES.MODE_CFB, iv)
    msg = iv + cipher.encrypt(bfile)
    return msg

    
def decode_AES(bfile,AES_key):
    q = bfile
    iv = q[:AES.block_size]
    cipher = AES.new(AES_key, AES.MODE_CFB, iv)
    msg = cipher.decrypt(q)[len(iv):]
    return msg
    
